<span class="line"><span style="color:#F0F3F6;">ctx.</span><span style="color:#DBB7FF;">arc</span><span style="color:#F0F3F6;">(</span><span style="color:#91CBFF;">100</span><span style="color:#F0F3F6;">, </span><span style="color:#91CBFF;">20</span><span style="color:#F0F3F6;">, </span><span style="color:#91CBFF;">50</span><span style="color:#F0F3F6;">, </span><span style="color:#91CBFF;">0</span><span style="color:#F0F3F6;">, Math.</span><span style="color:#91CBFF;">PI</span><span style="color:#F0F3F6;">, </span><span style="color:#91CBFF;">false</span><span style="color:#F0F3F6;">);</span></span>
