<span class="line"><span style="color:#F0F3F6;">  System.out.</span><span style="color:#DBB7FF;">println</span><span style="color:#F0F3F6;">(</span><span style="color:#ADDCFF;">&quot;教师名称：&quot;</span><span style="color:#FF9492;">+</span><span style="color:#F0F3F6;">teacher.name</span><span style="color:#FF9492;">+</span><span style="color:#ADDCFF;">&quot;</span><span style="color:#FF9492;">\\n</span><span style="color:#ADDCFF;">教师工资：&quot;</span><span style="color:#FF9492;">+</span><span style="color:#F0F3F6;">teacher.salary</span><span style="color:#FF9492;">+</span><span style="color:#ADDCFF;">&quot;</span><span style="color:#FF9492;">\\n</span><span style="color:#ADDCFF;">教师年龄：&quot;</span><span style="color:#FF9492;">+</span><span style="color:#F0F3F6;">teacher.age);</span></span>
