<span class="line"><span style="color:#F0F3F6;">      System.out.</span><span style="color:#DBB7FF;">print</span><span style="color:#F0F3F6;">(</span><span style="color:#FF9492;">new</span><span style="color:#DBB7FF;"> String</span><span style="color:#F0F3F6;">(b, </span><span style="color:#91CBFF;">0</span><span style="color:#F0F3F6;">, len, </span><span style="color:#ADDCFF;">&quot;UTF-8&quot;</span><span style="color:#F0F3F6;">));</span></span>
