<span class="line"><span style="color:#FF9492;">    -&gt;</span><span style="color:#FF9492;"> name</span><span style="color:#FF9492;"> VARCHAR</span><span style="color:#F0F3F6;">(</span><span style="color:#91CBFF;">22</span><span style="color:#F0F3F6;">) </span><span style="color:#FF9492;">NOT NULL</span><span style="color:#F0F3F6;">,</span></span>
